(2, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connect with your audience', 'Grow your customer base and increase your sales with marketing tools built for WooCommerce.', 'speaker', '{}', 'unactioned', 'woocommerce-admin', '2020-06-10 15:33:39', NULL, 0, '', NULL, 0),
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-06-10 15:33:40', NULL, 0, '', NULL, 0),
(4, 'wc-update-db-reminder', 'update', 'en_US', 'Yêu cầu cập nhật cơ sở dữ liệu WooCommerce', 'WooCommerce đã được cập nhật! Để đảm bảo mọi thứ hoạt động trơn tru, chúng tôi cần cập nhật cơ sở dữ liệu lên bản mới nhất. Quá trình cập nhật cơ sở dữ liệu sẽ chạy nền và sẽ mất chút thời gian, hãy kiên nhẫn. Người dùng cao cấp có thể cập nhật thay thế qua <a href=\"https://github.com/woocommerce/woocommerce/wiki/Upgrading-the-database-using-WP-CLI\">WP CLI</a>.', 'info', '{}', 'unactioned', 'woocommerce-core', '2020-04-22 08:33:47', NULL, 0, 'plain', NULL, 0),
(5, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-07-01 14:01:14', NULL, 0, '', NULL, 0),
(7, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', 'info', '{}', 'pending', 'woocommerce.com', '2020-10-08 02:46:54', NULL, 0, 'plain', '', 0),
(8, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', 'info', '{}', 'pending', 'woocommerce.com', '2020-10-08 02:46:54', NULL, 0, 'plain', '', 0),
(9, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', 'info', '{}', 'pending', 'woocommerce.com', '2020-10-08 02:46:54', NULL, 0, 'plain', '', 0),
(10, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Give feedback', 'Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2021-03-26 09:47:54', NULL, 0, 'plain', '', 0),
(11, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Did you know?', 'A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?', 'info', '{}', 'unactioned', 'woocommerce-admin', '2021-03-26 09:47:54', NULL, 0, 'plain', '', 0),
(13, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2021-03-26 09:47:54', NULL, 0, 'plain', '', 0),
(14, 'facebook_pixel_api_2021', 'marketing', 'en_US', 'Improve the performance of your Facebook ads', 'Enable Facebook Pixel and Conversions API through the latest version of Facebook for WooCommerce for improved performance and measurement of your Facebook ad campaigns.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(15, 'facebook_ec_2021', 'marketing', 'en_US', 'Sync your product catalog with Facebook to help boost sales', 'A single click adds all products to your Facebook Business Page shop. Product changes are automatically synced, with the flexibility to control which products are listed.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(16, 'wc-admin-getting-started-in-ecommerce', 'info', 'en_US', 'Getting Started in eCommerce - webinar', 'We want to make eCommerce and this process of getting started as easy as possible for you. Watch this webinar to get tips on how to have our store up and running in a breeze.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(17, 'your-first-product', 'info', 'en_US', 'Your first product', 'That\'s huge! You\'re well on your way to building a successful online store — now it’s time to think about how you\'ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(18, 'wc-square-apple-pay-boost-sales', 'marketing', 'en_US', 'Boost sales with Apple Pay', 'Now that you accept Apple Pay® with Square you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(19, 'wc-square-apple-pay-grow-your-business', 'marketing', 'en_US', 'Grow your business with Square and Apple Pay ', 'Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(20, 'wcpay-apple-pay-boost-sales', 'marketing', 'en_US', 'Boost sales with Apple Pay', 'Now that you accept Apple Pay® with WooCommerce Payments you can increase conversion rates by letting your customers know that Apple Pay® is available. Here’s a marketing guide to help you get started.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(21, 'wcpay-apple-pay-grow-your-business', 'marketing', 'en_US', 'Grow your business with WooCommerce Payments and Apple Pay', 'Now more than ever, shoppers want a fast, simple, and secure online checkout experience. Increase conversion rates by letting your customers know that you now accept Apple Pay®.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(22, 'wc-admin-optimizing-the-checkout-flow', 'info', 'en_US', 'Optimizing the checkout flow', 'It\'s crucial to get your store\'s checkout as smooth as possible to avoid losing sales. Let\'s take a look at how you can optimize the checkout experience for your shoppers.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(23, 'wc-admin-first-five-things-to-customize', 'info', 'en_US', 'The first 5 things to customize in your store', 'Deciding what to start with first is tricky. To help you properly prioritize, we\'ve put together this short list of the first few things you should customize in WooCommerce.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-19 23:47:54', NULL, 0, 'plain', '', 0),
(24, 'wcpay-apple-pay-is-now-available', 'marketing', 'en_US', 'Apple Pay is now available with WooCommerce Payments!', 'Increase your conversion rate by offering a fast and secure checkout with <a href=\"https://woocommerce.com/apple-pay/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">Apple Pay</a>®. It’s free to get started with <a href=\"https://woocommerce.com/payments/?utm_source=inbox&amp;utm_medium=product&amp;utm_campaign=wcpay_applepay\" target=\"_blank\">WooCommerce Payments</a>.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-26 11:32:42', NULL, 0, 'plain', '', 0),
(25, 'paypal_ppcp_gtm_2021', 'marketing', 'en_US', 'Offer more options with the new PayPal', 'Get the latest PayPal extension for a full suite of payment methods with extensive currency and country coverage.', 'info', '{}', 'pending', 'woocommerce.com', '2021-03-29 01:00:03', NULL, 0, 'plain', '', 0);
